definition.script:
pipeline {
    agent any
    tools{
        maven "mvn"
    }

    stages {
        stage('Initialize') {
            steps {
                sh '''
                  echo "PATH=${PATH}"
                  echo "M2_HOME= ${M2_HOME}"
                '''
            }
        }
        stage('Git Checkout') {
            steps {
                git(
                    url: 'https://github.com/victorhdcampos/product-app.git',
                    branch: 'main')
                script{
                    TAG_VERSION = sh(script: 'git rev-parse --short HEAD', returnStdout:true).trim()
                }
            }
        }
        stage('Build') {
             steps{
                 sh "mvn -DskipTests clean package"

             }
             post{
                 success {
                     sh 'cp target/product-0.0.1-SNAPSHOT.jar docker/'
                 }
             }
         }

    }
}
